INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U4_B3_I4', 'ingles-b1-U4', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''by them'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''by them'':","mastery_tag":"grammar:b1-passive","explanation":"El uso de ''has been'' muestra que la acción se completó en el pasado reciente, manteniendo el enfoque en el reporte.","stimulus_en":"They have finished the report.\n\nThe report has been finished... ...","correct_answer":"by them.","example":"by them","complexity":2,"concept_tags":["grammar","passive","unit"]}')
