VALUES ('B1_U5_B1_I3', 'ingles-b1-U5', 3, 'multiple_choice', 'Choose the correct answer for each question about conditional sentences.', '{"prompt_es":"Choose the correct answer for each question about conditional sentences.","mastery_tag":"grammar:b1-conditionals","explanation":"Aquí, ''will'' indica un resultado positivo en base a una acción real.","stimulus_en":"If she studies hard, she ___ pass the exam.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"should"},{"id":"o4","text":"might"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
