VALUES ('B1_U6_B2_I2', 'ingles-b1-U6', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''finish'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''finish'':","mastery_tag":"grammar:b1-passive","explanation":"Cuando cambiamos a la voz activa, el sujeto (el equipo) realiza la acción (terminar) directamente.","stimulus_en":"The project will be finished by the team next week.\n\nThe team will ...","correct_answer":"finish the project next week.","example":"finish","complexity":2,"concept_tags":["grammar","passive","unit"]}')
