VALUES ('B1_U6_B2_I4', 'ingles-b1-U6', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''Tom'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''Tom'':","mastery_tag":"grammar:b1-passive","explanation":"Aquí, la voz activa muestra que Tom realizó la acción de dar la presentación.","stimulus_en":"The presentation was given by Tom.\n\nTom gave ...","correct_answer":"the presentation.","example":"Tom","complexity":2,"concept_tags":["grammar","passive","unit"]}')
