INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U7_B1_I1', 'ingles-b1-U7', 1, 'fill_blanks', 'Complete the sentences with the correct words.', '{"prompt_es":"Complete the sentences with the correct words.","mastery_tag":"writing:b1-stories","main_text":"Last summer, I (1)___ to the beach with my friends. We (2)___ a lot of fun and (3)___ games all day. In the evening, we (4)___ a barbecue and (5)___ stories around the fire.","correct_answer":["went","had","played","had","told"],"explanation":"(1) La oración habla de un evento que ocurrió en el pasado (verbo en pasado).\n(2) El verbo ''have'' se usa en pasado para describir experiencias.\n(3) El verbo ''play'' en pasado describe actividades que hicimos.\n(4) Usamos ''have'' para hablar de eventos que disfrutamos.\n(5) El verbo ''tell'' en pasado se usa para compartir relatos.","complexity":2,"concept_tags":["writing","stories","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U7_B2_I1', 'ingles-b1-U7', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''GEORGE ORWELL'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''GEORGE ORWELL'':","mastery_tag":"grammar:b1-passive","explanation":"En la voz activa, el sujeto (George Orwell) realiza la acción (escribir) en lugar de recibirla.","stimulus_en":"The book was written by George Orwell.\n\nGeorge Orwell... ...","correct_answer":"wrote the book.","example":"GEORGE ORWELL","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U7_B2_I2', 'ingles-b1-U7', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''MY MOTHER'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''MY MOTHER'':","mastery_tag":"grammar:b1-passive","explanation":"Cuando cambiamos de voz pasiva a activa, el sujeto (mi madre) está realizando la acción (hacer).","stimulus_en":"The cake was made by my mother.\n\nMy mother... ...","correct_answer":"made the cake.","example":"MY MOTHER","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U7_B2_I3', 'ingles-b1-U7', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''ADELE'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''ADELE'':","mastery_tag":"grammar:b1-passive","explanation":"En esta transformación, cambiamos la voz pasiva a activa con el sujeto realizando la acción.","stimulus_en":"The song was sung by Adele.\n\nAdele... ...","correct_answer":"sang the song.","example":"ADELE","complexity":2,"concept_tags":["grammar","passive","unit"]}')
