INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U7_B2_I4', 'ingles-b1-U7', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''THE WORKERS'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''THE WORKERS'':","mastery_tag":"grammar:b1-passive","explanation":"Aquí, el sujeto (los trabajadores) realiza la acción (construir) en lugar de que la reciba.","stimulus_en":"The house was built by the workers.\n\nThe workers... ...","correct_answer":"built the house.","example":"THE WORKERS","complexity":2,"concept_tags":["grammar","passive","unit"]}')
