INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U7_B2_I5', 'ingles-b1-U7', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''PICASSO'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''PICASSO'':","mastery_tag":"grammar:b1-passive","explanation":"En esta oración, transformamos la voz pasiva a activa, mostrando quién realiza la acción.","stimulus_en":"The picture was painted by Picasso.\n\nPicasso... ...","correct_answer":"painted the picture.","example":"PICASSO","complexity":2,"concept_tags":["grammar","passive","unit"]}')
