INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U8_B2_I2', 'ingles-b1-U8', 7, 'multiple_choice', 'Choose the correct answer for each question related to interviews and discussions.', '{"prompt_es":"Choose the correct answer for each question related to interviews and discussions.","mastery_tag":"listening:b1-interviews","explanation":"Durante una entrevista, es importante mostrar tus habilidades para impresionar al entrevistador.","stimulus_en":"During an interview, it is important to ___ your skills.","options":[{"id":"o1","text":"ignore"},{"id":"o2","text":"show"},{"id":"o3","text":"hide"},{"id":"o4","text":"forget"}],"correct_answer":"o2","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
