VALUES ('B1_U8_B2_I3', 'ingles-b1-U8', 8, 'multiple_choice', 'Which question might you ask during an interview?', '{"prompt_es":"Which question might you ask during an interview?","mastery_tag":"listening:b1-interviews","explanation":"Preguntar sobre las fortalezas del candidato es relevante y útil en una entrevista.","stimulus_en":"","options":[{"id":"o1","text":"What is your name?"},{"id":"o2","text":"What are your strengths?"},{"id":"o3","text":"How old are you?"},{"id":"o4","text":"What do you eat?"}],"correct_answer":"o2","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
