VALUES ('B1_U8_B2_I5', 'ingles-b1-U8', 10, 'multiple_choice', 'What should you do if you don’t understand the interviewer?', '{"prompt_es":"What should you do if you don’t understand the interviewer?","mastery_tag":"listening:b1-interviews","explanation":"Si no entiendes al entrevistador, es mejor pedir aclaraciones para evitar malentendidos.","stimulus_en":"","options":[{"id":"o1","text":"Ignore them"},{"id":"o2","text":"Ask for clarification"},{"id":"o3","text":"Leave the room"},{"id":"o4","text":"Say nothing"}],"correct_answer":"o2","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
