VALUES ('B1_U8_B3_I5', 'ingles-b1-U8', 15, 'multiple_choice', 'Which device is commonly used for video calls?', '{"prompt_es":"Which device is commonly used for video calls?","mastery_tag":"vocabulary:b1-technology","explanation":"Una cámara es el dispositivo utilizado para capturar vídeo y se utiliza frecuentemente en las videollamadas.","stimulus_en":"","options":[{"id":"o1","text":"Camera"},{"id":"o2","text":"Scanner"},{"id":"o3","text":"Monitor"},{"id":"o4","text":"Router"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","technology","unit","grammar"]}')
