VALUES ('B1_U13_B1_I1', 'ingles-b1-U13', 1, 'multiple_choice', 'Which genre includes films that make you laugh?', '{"prompt_es":"Which genre includes films that make you laugh?","mastery_tag":"vocabulary:b1-entertainment","explanation":"La comedia es un género cinematográfico diseñado para hacer reír y entretener.","stimulus_en":"","options":[{"id":"o1","text":"Drama"},{"id":"o2","text":"Comedy"},{"id":"o3","text":"Horror"},{"id":"o4","text":"Thriller"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","entertainment","unit","grammar"]}')
