INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U13_B1_I2', 'ingles-b1-U13', 2, 'multiple_choice', 'What is a popular music festival held annually in the UK?', '{"prompt_es":"What is a popular music festival held annually in the UK?","mastery_tag":"vocabulary:b1-entertainment","explanation":"Glastonbury es uno de los festivales de música más famosos del mundo, conocido por su diversidad musical.","stimulus_en":"","options":[{"id":"o1","text":"Coachella"},{"id":"o2","text":"Glastonbury"},{"id":"o3","text":"Lollapalooza"},{"id":"o4","text":"Tomorrowland"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","entertainment","unit","grammar"]}')
