VALUES ('B1_U13_B1_I5', 'ingles-b1-U13', 5, 'multiple_choice', 'What is the term for a performance of music or drama in front of an audience?', '{"prompt_es":"What is the term for a performance of music or drama in front of an audience?","mastery_tag":"vocabulary:b1-entertainment","explanation":"Un ''show'' se refiere a una representación de música, danza o teatro para el público.","stimulus_en":"","options":[{"id":"o1","text":"Exhibition"},{"id":"o2","text":"Show"},{"id":"o3","text":"Display"},{"id":"o4","text":"Presentation"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","entertainment","unit","grammar"]}')
