INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U13_B3_I2', 'ingles-b1-U13', 8, 'multiple_choice', 'Which phrase is commonly used to start an informal letter?', '{"prompt_es":"Which phrase is commonly used to start an informal letter?","mastery_tag":"writing:b1-informal-letters","explanation":"La frase ''Hi [Name]'' es típica para comenzar cartas informales, ya que es amigable y personal.","stimulus_en":"","options":[{"id":"o1","text":"Dear Sir/Madam"},{"id":"o2","text":"To whom it may concern"},{"id":"o3","text":"Hi [Name]"},{"id":"o4","text":"Greetings"}],"correct_answer":"o3","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
