VALUES ('B1_U13_B3_I5', 'ingles-b1-U13', 11, 'multiple_choice', 'When writing to a friend, how can you express excitement?', '{"prompt_es":"When writing to a friend, how can you express excitement?","mastery_tag":"writing:b1-informal-letters","explanation":"Usar expresiones emocionales como ''I am very excited!'' es común en cartas informales para mostrar entusiasmo.","stimulus_en":"","options":[{"id":"o1","text":"I am very excited!"},{"id":"o2","text":"It is important to note"},{"id":"o3","text":"This is a reminder"},{"id":"o4","text":"Please remember"}],"correct_answer":"o1","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
