INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U14_B1_I1', 'ingles-b1-U14', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WAS WRITING'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WAS WRITING'':","mastery_tag":"grammar:b1-past-continuous","explanation":"En esta oración, debes usar el pasado continuo para describir una acción que estaba en progreso cuando ocurrió otra.","stimulus_en":"I (write) an email when you called me.\n\nI was writing... ...","correct_answer":"an email when you called me.","example":"WAS WRITING","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U14_B1_I2', 'ingles-b1-U14', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WAS DANCING'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WAS DANCING'':","mastery_tag":"grammar:b1-past-continuous","explanation":"Recuerda que el pasado continuo se usa para mostrar que una acción estaba en progreso en un momento específico en el pasado.","stimulus_en":"She (dance) when the music stopped.\n\nShe was dancing... ...","correct_answer":"when the music stopped.","example":"WAS DANCING","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U14_B1_I3', 'ingles-b1-U14', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WERE PLAYING'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WERE PLAYING'':","mastery_tag":"grammar:b1-past-continuous","explanation":"El pasado continuo se utiliza aquí para enfatizar que estaban jugando durante una acción paralela (la lluvia).","stimulus_en":"They (play) soccer while it rained.\n\nThey were playing... ...","correct_answer":"soccer while it rained.","example":"WERE PLAYING","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U14_B1_I4', 'ingles-b1-U14', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WAS WATCHING'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WAS WATCHING'':","mastery_tag":"grammar:b1-past-continuous","explanation":"Aquí, el pasado continuo muestra que estaba viendo televisión en el momento de tu llegada.","stimulus_en":"He (watch) TV when I arrived.\n\nHe was watching... ...","correct_answer":"TV when I arrived.","example":"WAS WATCHING","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
