INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U14_B1_I5', 'ingles-b1-U14', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WERE STUDYING'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WERE STUDYING'':","mastery_tag":"grammar:b1-past-continuous","explanation":"El pasado continuo se usa aquí para mostrar que estaban estudiando mientras recibían una llamada.","stimulus_en":"We (study) when they called.\n\nWe were studying... ...","correct_answer":"when they called.","example":"WERE STUDYING","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
