INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U14_B2_I1', 'ingles-b1-U14', 6, 'fill_blanks', 'Complete the sentences with the correct form of the past continuous.', '{"prompt_es":"Complete the sentences with the correct form of the past continuous.","mastery_tag":"grammar:b1-past-continuous","explanation":"En esta oración, usamos el pasado continuo para indicar que la acción de hablar estaba en progreso cuando ocurrió otra acción (el sonido de la alarma).","stimulus_en":"During the meeting yesterday, several things happened. Fill in the blanks with the correct past continuous form.\n\nI ___ (talk) to my colleague when the fire alarm rang.","correct_answer":"was talking","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
