INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U14_B2_I5', 'ingles-b1-U14', 10, 'fill_blanks', 'Complete the sentences with the correct form of the past continuous.', '{"prompt_es":"Complete the sentences with the correct form of the past continuous.","mastery_tag":"grammar:b1-past-continuous","explanation":"En esta oración, se utiliza el pasado continuo para resaltar que la acción de trabajar estaba en progreso cuando se descubrió un error.","stimulus_en":"During the meeting yesterday, several things happened. Fill in the blanks with the correct past continuous form.\n\nThe team ___ (work) on the report when they found an error.","correct_answer":"was working","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
