VALUES ('B1_U15_B2_I1', 'ingles-b1-U15', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''READ'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''READ'':","mastery_tag":"grammar:b1-past-continuous","explanation":"La frase usa ''was'' para formar el pasado continuo con el verbo ''read''.","stimulus_en":"I was reading a book when you called.\n\nI... ...","correct_answer":"was reading a book when you called.","example":"READ","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
