VALUES ('B1_U15_B2_I2', 'ingles-b1-U15', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''PLAY'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''PLAY'':","mastery_tag":"grammar:b1-past-continuous","explanation":"El pasado continuo se forma con ''were'' y el verbo en -ing.","stimulus_en":"The kids were playing outside when it started to rain.\n\nThe kids... ...","correct_answer":"were playing outside when it started to rain.","example":"PLAY","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
