INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U17_B2_I1', 'ingles-b1-U17', 6, 'fill_blanks', 'Fill in the gaps using the correct form of the word provided in parentheses.

Base words: (1) POLLUTE, (2) RECYCLE, (3) CHANGE, (4) AWARE, (5) CONDITION', '{"prompt_es":"Fill in the gaps using the correct form of the word provided in parentheses.\n\nBase words: (1) POLLUTE, (2) RECYCLE, (3) CHANGE, (4) AWARE, (5) CONDITION","mastery_tag":"vocabulary:b1-environment","main_text":"The (1)___ (POLLUTE) of air and water is a major issue in today''s world. Many countries are taking steps towards (2)___ (RECYCLE) to reduce waste. Climate (3)___ (CHANGE) affects ecosystems and species around the globe. People need to be more (4)___ (AWARE) of their environmental impact. By working together, we can improve (5)___ (CONDITION) for future generations.","correct_answer":["POLLUTION","RECYCLING","CHANGE","AWARENESS","CONDITIONS"],"explanation":"(1) Pollution es el término que se utiliza para describir la contaminación del aire y el agua.\n(2) Reciclaje es el proceso de convertir desechos en nuevos materiales y objetos.\n(3) El cambio climático se refiere a las variaciones climáticas a largo plazo en la Tierra.\n(4) La conciencia es la comprensión y conocimiento de un tema, en este caso, el medio ambiente.\n(5) Las condiciones ambientales son las circunstancias que afectan la vida en la Tierra.","complexity":2,"concept_tags":["vocabulary","environment","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U17_B3_I1', 'ingles-b1-U17', 7, 'multiple_choice', 'What is the first step in writing a story?', '{"prompt_es":"What is the first step in writing a story?","mastery_tag":"writing:b1-stories","explanation":"El primer paso para escribir una historia es crear un entorno donde ocurren los eventos.","stimulus_en":"","options":[{"id":"o1","text":"Choose characters"},{"id":"o2","text":"Decide the ending"},{"id":"o3","text":"Create a setting"},{"id":"o4","text":"Write the dialogue"}],"correct_answer":"o3","complexity":2,"concept_tags":["writing","stories","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
