VALUES ('B1_U18_B1_I3', 'ingles-b1-U18', 3, 'multiple_choice', 'How does the interviewer encourage the interviewee to speak?', '{"prompt_es":"How does the interviewer encourage the interviewee to speak?","mastery_tag":"listening:b1-interviews","explanation":"El entrevistador utiliza preguntas abiertas para alentar al entrevistado a hablar más.","stimulus_en":"","options":[{"id":"o1","text":"By asking closed questions"},{"id":"o2","text":"By interrupting often"},{"id":"o3","text":"By offering compliments"},{"id":"o4","text":"By using open-ended questions"}],"correct_answer":"o4","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
