VALUES ('B1_U20_B3_I4', 'ingles-b1-U20', 10, 'multiple_choice', 'Choose the correct relative clause to complete each sentence.', '{"prompt_es":"Choose the correct relative clause to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''Where'' es el adecuado porque estamos hablando de un lugar donde ocurrió algo.","stimulus_en":"This is the restaurant ___ we had dinner last week.","options":[{"id":"o1","text":"when"},{"id":"o2","text":"what"},{"id":"o3","text":"where"},{"id":"o4","text":"who"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
