INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U23_B1_I1', 'ingles-b1-U23', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''cook'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''cook'':","mastery_tag":"grammar:b1-passive","explanation":"En la voz pasiva, el objeto de la oración activa se convierte en el sujeto de la oración pasiva.","stimulus_en":"The chef cooks the meal.\n\nThe meal is cooked... ...","correct_answer":"by the chef.","example":"cook","complexity":2,"concept_tags":["grammar","passive","unit"]}')
