VALUES ('B1_U23_B3_I2', 'ingles-b1-U23', 12, 'multiple_choice', 'Choose the correct option to complete each sentence using the passive voice.', '{"prompt_es":"Choose the correct option to complete each sentence using the passive voice.","mastery_tag":"grammar:b1-passive","explanation":"Usamos ''was given'' porque la acción ocurrió en el pasado y es pasiva.","stimulus_en":"The presentation ___ by the manager yesterday.","options":[{"id":"o1","text":"was given"},{"id":"o2","text":"is given"},{"id":"o3","text":"gave"},{"id":"o4","text":"giving"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
