INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B1_I1', 'ingles-b1-U24', 1, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"Se usa ''will'' en la primera condicional para situaciones reales en el futuro.","stimulus_en":"If it rains tomorrow, we ___ stay at home.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"shall"},{"id":"o4","text":"might"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B1_I2', 'ingles-b1-U24', 2, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"Se utiliza ''would'' en la segunda condicional para situaciones hipotéticas.","stimulus_en":"If I had a million dollars, I ___ travel the world.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"can"},{"id":"o4","text":"might"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B1_I3', 'ingles-b1-U24', 3, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"En este caso, ''will'' se usa para acciones futuras que dependen de una condición.","stimulus_en":"If I see her at the party, I ___ tell her the news.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"should"},{"id":"o4","text":"could"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B1_I4', 'ingles-b1-U24', 4, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"''Would have'' se utiliza para situaciones pasadas que no sucedieron.","stimulus_en":"If he had known about the meeting, he ___ come.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would have"},{"id":"o3","text":"would"},{"id":"o4","text":"might have"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B1_I5', 'ingles-b1-U24', 5, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"''Will'' indica una acción futura que ocurrirá si se cumple una condición.","stimulus_en":"If you call me, I ___ help you with your homework.","options":[{"id":"o1","text":"might"},{"id":"o2","text":"will"},{"id":"o3","text":"would"},{"id":"o4","text":"should"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B2_I1', 'ingles-b1-U24', 6, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"En este caso, ''that'' se usa para definir el libro específico que me prestaste.","stimulus_en":"The book ___ you lent me is fantastic.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"which"},{"id":"o3","text":"that"},{"id":"o4","text":"when"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B2_I2', 'ingles-b1-U24', 7, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Aquí, ''who'' se utiliza para añadir información sobre mi hermana.","stimulus_en":"My sister, ___ is a doctor, lives in London.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"which"},{"id":"o3","text":"where"},{"id":"o4","text":"when"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B2_I3', 'ingles-b1-U24', 8, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''Where'' se usa para indicar el lugar donde sucedió algo.","stimulus_en":"The restaurant ___ we dined is famous for its pizza.","options":[{"id":"o1","text":"where"},{"id":"o2","text":"which"},{"id":"o3","text":"that"},{"id":"o4","text":"who"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B2_I4', 'ingles-b1-U24', 9, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''When'' se utiliza para definir un tiempo específico en una oración.","stimulus_en":"I will never forget the day ___ we met.","options":[{"id":"o1","text":"when"},{"id":"o2","text":"who"},{"id":"o3","text":"that"},{"id":"o4","text":"where"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B2_I5', 'ingles-b1-U24', 10, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''That'' se usa aquí para especificar las películas que quiero ver.","stimulus_en":"There are many movies ___ I want to watch.","options":[{"id":"o1","text":"which"},{"id":"o2","text":"that"},{"id":"o3","text":"who"},{"id":"o4","text":"when"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B3_I1', 'ingles-b1-U24', 11, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''which'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''which'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"En esta oración, usamos ''which'' para añadir información sobre el restaurante. Es una cláusula relativa que especifica cuál restaurante.","stimulus_en":"The restaurant is very popular. It is in the city center.\n\nThe restaurant, which... ...","correct_answer":"is very popular, is in the city center.","example":"which","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B3_I2', 'ingles-b1-U24', 12, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''who'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''who'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Aquí usamos ''who'' porque hablamos de una persona, y queremos añadir información sobre ella.","stimulus_en":"She is a doctor. She helps people.\n\nShe is a doctor who... ...","correct_answer":"helps people.","example":"who","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B3_I3', 'ingles-b1-U24', 13, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''that'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''that'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Utilizamos ''that'' para dar más información sobre el museo, que es una cosa.","stimulus_en":"I visited the museum. It has ancient artifacts.\n\nI visited the museum that... ...","correct_answer":"has ancient artifacts.","example":"that","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B3_I4', 'ingles-b1-U24', 14, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''which'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''which'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Aquí, ''which'' añade información sobre el libro, indicando quién lo escribió.","stimulus_en":"The book is on the table. It is written by J.K. Rowling.\n\nThe book, which... ...","correct_answer":"is written by J.K. Rowling, is on the table.","example":"which","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B3_I5', 'ingles-b1-U24', 15, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''when'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''when'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Utilizamos ''when'' para referirnos a un momento específico en el tiempo, que es el día.","stimulus_en":"I remember the day. We met on that day.\n\nI remember the day when... ...","correct_answer":"we met.","example":"when","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

