VALUES ('B1_U26_B1_I3', 'ingles-b1-U26', 3, 'multiple_choice', 'Choose the correct option to complete each sentence using relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence using relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"La opción correcta es ''where'' porque se refiere a un lugar. ''Where'' se usa para hablar de locaciones.","stimulus_en":"The restaurant ____ we went to last night was amazing.","options":[{"id":"o1","text":"when"},{"id":"o2","text":"where"},{"id":"o3","text":"who"},{"id":"o4","text":"that"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","relative","clauses","animal","kingdom"]}')
