VALUES ('B1_U26_B1_I4', 'ingles-b1-U26', 4, 'multiple_choice', 'Choose the correct option to complete each sentence using relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence using relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''When'' es la opción correcta aquí porque se refiere a un tiempo. Se usa para describir momentos en el tiempo.","stimulus_en":"I remember the day ____ we first met.","options":[{"id":"o1","text":"which"},{"id":"o2","text":"that"},{"id":"o3","text":"who"},{"id":"o4","text":"when"}],"correct_answer":"o4","complexity":2,"concept_tags":["grammar","relative","clauses","animal","kingdom"]}')
