VALUES ('B1_U26_B1_I5', 'ingles-b1-U26', 5, 'multiple_choice', 'Choose the correct option to complete each sentence using relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence using relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''That'' es correcto porque especifica el coche del que estamos hablando. ''That'' se usa para definir o identificar.","stimulus_en":"The car ____ is parked outside is my brother''s.","options":[{"id":"o1","text":"which"},{"id":"o2","text":"that"},{"id":"o3","text":"who"},{"id":"o4","text":"when"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","relative","clauses","animal","kingdom"]}')
