VALUES ('B1_U26_B2_I4', 'ingles-b1-U26', 9, 'multiple_choice', 'Choose the correct answer to complete each sentence using the past continuous tense.', '{"prompt_es":"Choose the correct answer to complete each sentence using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"Usamos ''were not listening'' para formar la negativa en pasado continuo.","stimulus_en":"They _____ (not / listen) to the teacher when the bell rang.","options":[{"id":"o1","text":"were not listening"},{"id":"o2","text":"did not listen"},{"id":"o3","text":"not listened"},{"id":"o4","text":"not listening"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","animal","kingdom"]}')
