INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U26_B3_I1', 'ingles-b1-U26', 11, 'fill_blanks', 'Complete the sentences using the correct relative clause.', '{"prompt_es":"Complete the sentences using the correct relative clause.","mastery_tag":"grammar:b1-relative-clauses","main_text":"She is the musician (1)___ plays the piano beautifully.\n\nThis is the restaurant (2)___ we had dinner last night.\n\nThe book (3)___ I borrowed from you is very interesting.\n\nMy brother, (4)___ lives in Canada, is visiting us next month.\n\nI remember the day (5)___ we first met.","correct_answer":["who","where","that","who","when"],"explanation":"(1) Utilizamos ''who'' para referirnos a personas.\n(2) ''Where'' se usa para referirnos a lugares.\n(3) ''That'' es común para referirse a cosas o objetos.\n(4) ''Who'' se usa para dar más información sobre una persona específica.\n(5) ''When'' se usa para referirse a momentos o fechas.","complexity":2,"concept_tags":["grammar","relative","clauses","animal","kingdom"]}')
