VALUES ('B1_U27_B2_I5', 'ingles-b1-U27', 10, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''That'' se utiliza para referirse a cosas, como una película, en cláusulas relativas.","stimulus_en":"The movie ___ you recommended was very good.","options":[{"id":"o1","text":"which"},{"id":"o2","text":"who"},{"id":"o3","text":"that"},{"id":"o4","text":"where"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
