INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U29_B1_I4', 'ingles-b1-U29', 4, 'multiple_choice', 'Choose the correct option to complete the sentences about reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences about reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"Usamos ''said'' para reportar afirmaciones en el discurso indirecto.","stimulus_en":"They said, ''We are happy.'' They _____ that they were happy.","options":[{"id":"o1","text":"said"},{"id":"o2","text":"told"},{"id":"o3","text":"reported"},{"id":"o4","text":"mentioned"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
