VALUES ('B1_U29_B1_I5', 'ingles-b1-U29', 5, 'multiple_choice', 'Choose the correct option to complete the sentences about reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences about reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"Usamos ''told'' cuando indicamos a alguien que haga algo.","stimulus_en":"She told me, ''You must study harder.'' She _____ me that I had to study harder.","options":[{"id":"o1","text":"said"},{"id":"o2","text":"told"},{"id":"o3","text":"reminded"},{"id":"o4","text":"taught"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
