INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U32_B1_I1', 'ingles-b1-U32', 1, 'multiple_choice', 'I think remote work is more effective than working in the office. What is your opinion?', '{"prompt_es":"I think remote work is more effective than working in the office. What is your opinion?","mastery_tag":"speaking:b1-expressing-opinions","explanation":"En este contexto, ''I agree'' expresa que estás de acuerdo con la opinión presentada sobre el trabajo remoto.","stimulus_en":"","options":[{"id":"o1","text":"I agree"},{"id":"o2","text":"I don''t think so"},{"id":"o3","text":"It''s not possible"},{"id":"o4","text":"I prefer the office"}],"correct_answer":"o1","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U32_B1_I2', 'ingles-b1-U32', 2, 'multiple_choice', 'Some people believe that team meetings are a waste of time. How do you feel about this?', '{"prompt_es":"Some people believe that team meetings are a waste of time. How do you feel about this?","mastery_tag":"speaking:b1-expressing-opinions","explanation":"Aquí, ''I think they are important'' expresa una opinión positiva sobre la importancia de las reuniones de equipo.","stimulus_en":"","options":[{"id":"o1","text":"I think they are important"},{"id":"o2","text":"I feel the same"},{"id":"o3","text":"It''s interesting"},{"id":"o4","text":"I don''t have a view"}],"correct_answer":"o1","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U32_B1_I3', 'ingles-b1-U32', 3, 'multiple_choice', 'Some experts argue that social media can harm productivity. What do you think?', '{"prompt_es":"Some experts argue that social media can harm productivity. What do you think?","mastery_tag":"speaking:b1-expressing-opinions","explanation":"En este caso, ''I don''t agree'' es una forma de expresar desacuerdo con la opinión de que las redes sociales perjudican la productividad.","stimulus_en":"","options":[{"id":"o1","text":"That''s true"},{"id":"o2","text":"I don''t agree"},{"id":"o3","text":"I don''t use social media"},{"id":"o4","text":"It depends"}],"correct_answer":"o2","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U32_B1_I4', 'ingles-b1-U32', 4, 'multiple_choice', 'In my view, professional development is essential for career growth. How do you feel?', '{"prompt_es":"In my view, professional development is essential for career growth. How do you feel?","mastery_tag":"speaking:b1-expressing-opinions","explanation":"La respuesta ''I completely agree'' muestra un fuerte apoyo a la afirmación sobre el desarrollo profesional.","stimulus_en":"","options":[{"id":"o1","text":"I completely agree"},{"id":"o2","text":"I have no opinion"},{"id":"o3","text":"It''s optional"},{"id":"o4","text":"I disagree"}],"correct_answer":"o1","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U32_B1_I5', 'ingles-b1-U32', 5, 'multiple_choice', 'Many people say that work-life balance is crucial. What is your stance?', '{"prompt_es":"Many people say that work-life balance is crucial. What is your stance?","mastery_tag":"speaking:b1-expressing-opinions","explanation":"Elegir ''I think it''s crucial'' indica un fuerte acuerdo con la importancia del equilibrio entre trabajo y vida personal.","stimulus_en":"","options":[{"id":"o1","text":"I understand their point"},{"id":"o2","text":"It''s not important"},{"id":"o3","text":"I disagree"},{"id":"o4","text":"I think it''s crucial"}],"correct_answer":"o4","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U32_B2_I1', 'ingles-b1-U32', 6, 'multiple_choice', 'How long ______ you been working on this project?', '{"prompt_es":"How long ______ you been working on this project?","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"En esta oración, ''you'' requiere ''have'' en presente perfecto continuo.","stimulus_en":"","options":[{"id":"o1","text":"have"},{"id":"o2","text":"has"},{"id":"o3","text":"did"},{"id":"o4","text":"do"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
