INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U32_B2_I3', 'ingles-b1-U32', 8, 'multiple_choice', 'Choose the correct answer for each question related to the present perfect continuous tense.', '{"prompt_es":"Choose the correct answer for each question related to the present perfect continuous tense.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"La oración habla de un tiempo que continúa, así que usamos ''have been''.","stimulus_en":"They ______ working on the report for two weeks.","options":[{"id":"o1","text":"have been"},{"id":"o2","text":" has been"},{"id":"o3","text":"are"},{"id":"o4","text":"were"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
