INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U32_B2_I5', 'ingles-b1-U32', 10, 'multiple_choice', 'What ______ you been doing all day?', '{"prompt_es":"What ______ you been doing all day?","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"''You'' requiere ''have'' en presente perfecto continuo.","stimulus_en":"","options":[{"id":"o1","text":"have"},{"id":"o2","text":"has"},{"id":"o3","text":"do"},{"id":"o4","text":"did"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
