VALUES ('B1_U33_B1_I1', 'ingles-b1-U33', 1, 'multiple_choice', 'Choose the correct option to complete each sentence with the present perfect continuous tense.', '{"prompt_es":"Choose the correct option to complete each sentence with the present perfect continuous tense.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Usamos ''have been working'' porque estamos hablando de una acción que comenzó en el pasado y continúa hasta ahora.","stimulus_en":"I ___ (work) on this project for three weeks.","options":[{"id":"o1","text":"have worked"},{"id":"o2","text":"have been working"},{"id":"o3","text":"worked"},{"id":"o4","text":"has been working"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
