INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U34_B2_I1', 'ingles-b1-U34', 6, 'multiple_choice', 'Choose the correct passive form of the sentences below.', '{"prompt_es":"Choose the correct passive form of the sentences below.","mastery_tag":"grammar:b1-passive","explanation":"La forma pasiva se usa aquí, indicando que el libro fue escrito por J.K. Rowling en el pasado.","stimulus_en":"The book ___ by J.K. Rowling.","options":[{"id":"o1","text":"was written"},{"id":"o2","text":"is written"},{"id":"o3","text":"writes"},{"id":"o4","text":"written"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
