INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U34_B2_I2', 'ingles-b1-U34', 7, 'multiple_choice', 'Choose the correct passive form of the sentences below.', '{"prompt_es":"Choose the correct passive form of the sentences below.","mastery_tag":"grammar:b1-passive","explanation":"La oración está en tiempo presente, por lo que usamos ''is sung'' para la voz pasiva.","stimulus_en":"The song ___ by many artists.","options":[{"id":"o1","text":"is sung"},{"id":"o2","text":"sang"},{"id":"o3","text":"was sung"},{"id":"o4","text":"sings"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
