INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U34_B2_I5', 'ingles-b1-U34', 10, 'multiple_choice', 'Choose the correct passive form of the sentences below.', '{"prompt_es":"Choose the correct passive form of the sentences below.","mastery_tag":"grammar:b1-passive","explanation":"La acción de diseñar se realizó en el pasado, por lo tanto, usamos ''was designed''.","stimulus_en":"This building ___ by architects in 1990.","options":[{"id":"o1","text":"was designed"},{"id":"o2","text":"designed"},{"id":"o3","text":"is designed"},{"id":"o4","text":"designs"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
