VALUES ('B1_U35_B1_I5', 'ingles-b1-U35', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''that'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''that'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Usamos ''that'' para identificar o marcar objetos específicos.","stimulus_en":"The book is on the table. I bought it yesterday.\n\nThe book that ...","correct_answer":"is on the table, I bought yesterday.","example":"that","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
