VALUES ('B1_U36_B1_I1', 'ingles-b1-U36', 1, 'multiple_choice', 'Choose the best option to complete each sentence about informal letters and emails.', '{"prompt_es":"Choose the best option to complete each sentence about informal letters and emails.","mastery_tag":"writing:b1-informal-letters","explanation":"En las cartas informales, normalmente usamos saludos como ''Hi'' o ''Hello'', no ''Dear Sir'' o ''To whom it may concern'', que son más formales.","stimulus_en":"When starting an informal letter, you usually write _____.","options":[{"id":"o1","text":"Dear Sir"},{"id":"o2","text":"Hi"},{"id":"o3","text":"To whom it may concern"},{"id":"o4","text":"Hello Mr."}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B1_I2', 'ingles-b1-U36', 2, 'multiple_choice', 'Choose the best option to complete each sentence about informal letters and emails.', '{"prompt_es":"Choose the best option to complete each sentence about informal letters and emails.","mastery_tag":"writing:b1-informal-letters","explanation":"En un email informal, es común preguntar sobre la familia o la vida personal de un amigo, a diferencia de asuntos de negocios o legales.","stimulus_en":"In an informal email, you might ask about _____.","options":[{"id":"o1","text":"business matters"},{"id":"o2","text":"your friend''s family"},{"id":"o3","text":"company policies"},{"id":"o4","text":"legal issues"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B1_I3', 'ingles-b1-U36', 3, 'multiple_choice', 'Choose the best option to complete each sentence about informal letters and emails.', '{"prompt_es":"Choose the best option to complete each sentence about informal letters and emails.","mastery_tag":"writing:b1-informal-letters","explanation":"Cierres como ''Best wishes'' son apropiados para cartas informales. ''Sincerely'' y ''Yours faithfully'' son más formales.","stimulus_en":"To close an informal letter, you can say _____.","options":[{"id":"o1","text":"Sincerely"},{"id":"o2","text":"Warm regards"},{"id":"o3","text":"Best wishes"},{"id":"o4","text":"Yours faithfully"}],"correct_answer":"o3","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
