VALUES ('B1_U36_B1_I4', 'ingles-b1-U36', 4, 'multiple_choice', 'Which of the following is NOT typical in an informal letter? _____.', '{"prompt_es":"Which of the following is NOT typical in an informal letter? _____.","mastery_tag":"writing:b1-informal-letters","explanation":"En las cartas informales, evitamos un tono formal y usamos un lenguaje más relajado y personal.","stimulus_en":"","options":[{"id":"o1","text":"Sharing personal news"},{"id":"o2","text":"Using slang"},{"id":"o3","text":"Writing in a formal tone"},{"id":"o4","text":"Asking for opinions"}],"correct_answer":"o3","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
