VALUES ('B1_U36_B1_I5', 'ingles-b1-U36', 5, 'multiple_choice', 'Choose the best option to complete each sentence about informal letters and emails.', '{"prompt_es":"Choose the best option to complete each sentence about informal letters and emails.","mastery_tag":"writing:b1-informal-letters","explanation":"Incluir historias divertidas hace que la carta sea más amena y personal, lo cual es adecuado para amigos.","stimulus_en":"When writing to a friend, it’s important to include _____.","options":[{"id":"o1","text":"professional updates"},{"id":"o2","text":"funny stories"},{"id":"o3","text":"bank details"},{"id":"o4","text":"strict rules"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
