VALUES ('B1_U36_B3_I2', 'ingles-b1-U36', 12, 'multiple_choice', 'Choose the correct option to complete each sentence.', '{"prompt_es":"Choose the correct option to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Usamos ''who'' para referirnos a personas, en este caso, ''the teacher''.","stimulus_en":"She is the teacher ____ explains math well.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"that"},{"id":"o3","text":"when"},{"id":"o4","text":"which"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
