VALUES ('B1_U36_B3_I3', 'ingles-b1-U36', 13, 'multiple_choice', 'Choose the correct option to complete each sentence.', '{"prompt_es":"Choose the correct option to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Utilizamos ''that'' para hablar sobre un lugar específico, en este caso, ''the restaurant''.","stimulus_en":"The restaurant ____ we visited last week was amazing.","options":[{"id":"o1","text":"where"},{"id":"o2","text":"who"},{"id":"o3","text":"that"},{"id":"o4","text":"when"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
