VALUES ('B1_U37_B1_I1', 'ingles-b1-U37', 1, 'multiple_choice', 'Choose the correct answer to complete each sentence using the Present Perfect Continuous tense.', '{"prompt_es":"Choose the correct answer to complete each sentence using the Present Perfect Continuous tense.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"La respuesta correcta es ''have been studying'' porque se trata de una acción que comenzó en el pasado y continúa hasta el presente.","stimulus_en":"They ___ (study) for three hours.","options":[{"id":"o1","text":"have been studying"},{"id":"o2","text":"have studied"},{"id":"o3","text":"are studying"},{"id":"o4","text":"studied"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
